'\x3cdiv id \x3d "section_5"\x3e\x3cspan id \x3d "Examples"\x3e\x3c/span\x3e\x3ch3 class \x3d "editable"\x3eExamples\x3c/h3\x3e\n\x3cdiv id \x3d "section_6"\x3e\x3cspan id \x3d "Example:_Throw_an_object"\x3e\x3c/span\x3e\x3ch4 class \x3d "editable"\x3eExample: Throw an object\x3c/h4\x3e\n\x3cp\x3eYou can specify an object when you throw an exception. You can then reference the object\'s properties in the \x3ccode\x3ecatch\x3c/code\x3e block. The following example creates an object \x3ccode\x3emyUserException\x3c/code\x3e of type \x3ccode\x3eUserException\x3c/code\x3e and uses it in a \x3ccode\x3ethrow\x3c/code\x3e statement.\x3c/p\x3e\n\x3cpre\x3efunction UserException(message) {\n   this.message \x3d message;\n   this.name \x3d \x26quot;UserException\x26quot;;\n}\nfunction getMonthName(mo) {\n   mo \x3d mo-1; // Adjust month number for array index (1\x3dJan, 12\x3dDec)\n   var months \x3d new Array(\x26quot;Jan\x26quot;, \x26quot;Feb\x26quot;, \x26quot;Mar\x26quot;, \x26quot;Apr\x26quot;, \x26quot;May\x26quot;, \x26quot;Jun\x26quot;, \x26quot;Jul\x26quot;,\n      \x26quot;Aug\x26quot;, \x26quot;Sep\x26quot;, \x26quot;Oct\x26quot;, \x26quot;Nov\x26quot;, \x26quot;Dec\x26quot;);\n   if (months[mo]\x26nbsp;!\x3d\x3d undefined) {\n      return months[mo];\n   } else {\n      myUserException \x3d new UserException(\x26quot;InvalidMonthNo\x26quot;);\n      throw myUserException;\n   }\n}\n\ntry {\n   // statements to try\n   var myMonth \x3d 15; // 15 is out of bound to raise the exception\n   monthName \x3d getMonthName(myMonth);\n} catch (e) {\n   monthName \x3d \x26quot;unknown\x26quot;;\n   logMyErrors(e.message, e.name); // pass exception object to err handler\n}\n\x3c/pre\x3e\n\x3c/div\x3e\x3cdiv id \x3d "section_7"\x3e\x3cspan id \x3d "Example:_Another_example_of_throwing_an_object"\x3e\x3c/span\x3e\x3ch4 class \x3d "editable"\x3eExample: Another example of throwing an object\x3c/h4\x3e\n\x3cp\x3eThe following example tests an input string for a U.S. zip code. If the zip code uses an invalid format, the throw statement throws an exception by creating an object of type \x3ccode\x3eZipCodeFormatException\x3c/code\x3e.\x3c/p\x3e\n\x3cpre\x3e/*\n * Creates a ZipCode object.\n *\n * Accepted formats for a zip code are:\n *    12345\n *    12345-6789\n *    123456789\n *    12345 6789\n *\n * If the argument passed to the ZipCode constructor does not\n * conform to one of these patterns, an exception is thrown.\n */\n\nfunction ZipCode(zip) {\n   zip \x3d new String(zip);\n   pattern \x3d /[0-9]{5}([- ]?[0-9]{4})?/;\n   if (pattern.test(zip)) {\n      // zip code value will be the first match in the string\n      this.value \x3d zip.match(pattern)[0];\n      this.valueOf \x3d function() {\n         return this.value\n      };\n      this.toString \x3d function() {\n         return String(this.value)\n      };\n   } else {\n      throw new ZipCodeFormatException(zip);\n   }\n}\n\nfunction ZipCodeFormatException(value) {\n   this.value \x3d value;\n   this.message \x3d \x26quot;does not conform to the expected format for a zip code\x26quot;;\n   this.toString \x3d function() {\n      return this.value + this.message\n   };\n}\n\n/*\n * This could be in a script that validates address data\n * for US addresses.\n */\n\nvar ZIPCODE_INVALID \x3d -1;\nvar ZIPCODE_UNKNOWN_ERROR \x3d -2;\n\nfunction verifyZipCode(z) {\n   try {\n      z \x3d new ZipCode(z);\n   } catch (e) {\n      if (e instanceof ZipCodeFormatException) {\n         return ZIPCODE_INVALID;\n      } else {\n         return ZIPCODE_UNKNOWN_ERROR;\n      }\n   }\n   return z;\n}\n\na \x3d verifyZipCode(95060);         // returns 95060\nb \x3d verifyZipCode(9560;)          // returns -1\nc \x3d verifyZipCode(\x26quot;a\x26quot;);           // returns -1\nd \x3d verifyZipCode(\x26quot;95060\x26quot;);       // returns 95060\ne \x3d verifyZipCode(\x26quot;95060 1234\x26quot;);  // returns 95060 1234\n\x3c/pre\x3e\n\x3c/div\x3e\x3cdiv id \x3d "section_8"\x3e\x3cspan id \x3d "Example:_Rethrow_an_exception"\x3e\x3c/span\x3e\x3ch4 class \x3d "editable"\x3eExample: Rethrow an exception\x3c/h4\x3e\n\x3cp\x3eYou can use \x3ccode\x3ethrow\x3c/code\x3e to rethrow an exception after you catch it. The following example catches an exception with a numeric value and rethrows it if the value is over 50. The rethrown exception propagates up to the enclosing function or to the top level so that the user sees it.\x3c/p\x3e\n\x3cpre class \x3d "eval"\x3etry {\n   throw n; // throws an exception with a numeric value\n} catch (e) {\n   if (e \x26lt;\x3d 50) {\n      // statements to handle exceptions 1-50\n   } else {\n      // cannot handle this exception, so rethrow\n      throw e;\n   }\n}\n\x3c/pre\x3e\n\x3c/div\x3e\x3c/div\x3e',
