class SQLConfig
{
    public static $cache = null;

    public static function setCache($query,$valid)
    {
        if(!SQLConfig::getFromCache($query)){
            return SQLConfig::$cache->set_cache(md5($query).'.q',$valid);
        }
    }

    public static function getFromCache($query)
    {

        if(SQLConfig::$cache==null){
            SQLConfig::$cache = new SimpleCache();
            SQLConfig::$cache->cache_time=1000;

        }
        return SQLConfig::$cache->get_cache(md5($query).'.q');
    }

